sys_script_include.script:
var GetPRGEmployeeInformation = Class.create();
GetPRGEmployeeInformation.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getPRGuserInfo: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		var employeeid = this.newItem("EmployeeID");
		employeeid.setAttribute("value",userRecord.x_230562_prgorgcha_employeeid);
		var companyNum = this.newItem("CompanyNumber");
		companyNum.setAttribute("value",userRecord.x_230562_prgorgcha_companynum);
		var employeeStatus = this.newItem("EmpStatus");
		employeeStatus.setAttribute("value",userRecord.x_230562_prgorgcha_empstatus);
		var HiredDate = this.newItem("HiredDate");
		var newDate;
		if(userRecord.x_230562_prgorgcha_hireddate!='')
		{
			//newDate = userRecord.x_230562_prgorgcha_hireddate.substr(0,10);
			newDate = userRecord.x_230562_prgorgcha_hireddate;
		}
		else
		{
			newDate = userRecord.x_230562_prgorgcha_hireddate;
		}
		HiredDate.setAttribute("value",newDate);
		var JobCode = this.newItem("JobCode");
		JobCode.setAttribute("value",userRecord.x_230562_prgorgcha_jobcode);
		var LidTitle = this.newItem("LidTitle");
		LidTitle.setAttribute("value",userRecord.x_230562_prgorgcha_lidtitle);
		var Title = this.newItem("Title");
		Title.setAttribute("value",userRecord.title);
		var NickName = this.newItem("NickName");
		NickName.setAttribute("value", userRecord.x_230562_prgorgcha_nickname);
		var FirtName = this.newItem("FirstName");
		FirtName.setAttribute("value", userRecord.first_name);
		var LastName = this.newItem("LastName");
		LastName.setAttribute("value", userRecord.last_name);
		var PayGrade = this.newItem("PayGrade");
		PayGrade.setAttribute("value", userRecord.x_230562_prgorgcha_paygrade);
		var IsExempt = this.newItem("IsExempt");
		IsExempt.setAtrribute("value", userRecord.x_230562_prgorgcha_isexempt);
	},
    type: 'GetPRGEmployeeInformation'
});
