sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	// Add your code here
	if(source.u_nickname==''){
	target.user_name = source.u_first_name+' '+source.u_last_name+'('+source.u_employeeid+')';
	}else
	{
		target.user_name = source.u_nickname+' '+source.u_last_name+'('+source.u_employeeid+')';
	}
	if(!source.u_employeeid.toString().StartWith('-'))
	{
		target.employee_number = 'PRG'+source.u_employeeid;
	}
	else
	{
		target.employee_number = 'PRG(C)'+source.u_employeeid;
	}
})(source, target, map, log, action==="update");
